"use client"

import { MenuFoldOutlined, MenuUnfoldOutlined } from "@ant-design/icons"
import { faArchive, faFile } from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import {
    Avatar,
    Button,
    Dropdown,
    Flex,
    Image,
    Layout,
    Menu,
    Skeleton,
    theme
} from "antd"
import { useSession } from "next-auth/react"
import Link from "next/link"
import { useRouter } from "next/navigation"
import { PropsWithChildren, useContext, useState } from "react"
import { PathParamsContext } from "./_context/context"

const { Header, Sider, Content } = Layout

const CustomMenu = () => {
    const router = useRouter()

    const switchRoute = (e: string) => {
        const location = window.location.origin
        router.push(`${location}/bu/${e}`)
    }

    return (
        <Menu
            theme="dark"
            mode="inline"
            defaultSelectedKeys={["1"]}
            /*  onClick={(e) => {
                switchRoute(e.key)
            }} */
            items={[
                {
                    key: "template",
                    icon: (
                        <Link href={"/bu/template"}>
                            <FontAwesomeIcon icon={faFile} />
                        </Link>
                    ),
                    label: "Biểu mẫu"
                },
                /*   {
                    key: "template/new",
                    icon: (
                        <Link href={"/bu/template/new"}>
                            <FontAwesomeIcon icon={faFileCirclePlus} />
                        </Link>
                    ),
                    label: "Tạo mới biểu mẫu"
                }, */
                {
                    key: "mywork",
                    icon: (
                        <Link href={"/bu/mywork"}>
                            <FontAwesomeIcon icon={faArchive} />
                        </Link>
                    ),
                    label: "Công việc của tôi"
                }
            ]}
        />
    )
}

const SideMenu = ({ children }: PropsWithChildren) => {
    const [collapsed, setCollapsed] = useState(false)
    const { documentName, pathName } = useContext(PathParamsContext)

    const { status, data: session } = useSession()

    const {
        token: { colorBgContainer }
    } = theme.useToken()

    if (status === "loading")
        return <Skeleton.Avatar shape="circle" active={true} />

    if (status === "unauthenticated")
        return (
            <Link className="nav-link" href="api/auth/signin">
                Login
            </Link>
        )

    return (
        <Layout className="h-screen">
            <Sider trigger={null} collapsible collapsed={collapsed}>
                <center>
                    <Image
                        className="mt-2 mb-2"
                        width="40%"
                        preview={false}
                        src={`/img/hptIconKnowingIT.png`}
                        fallback="data:image/png;base64,"
                        alt="Icon"
                    />
                </center>
                <CustomMenu />
            </Sider>
            <Layout>
                <Header
                    style={{
                        padding: 0,
                        background: colorBgContainer
                    }}
                >
                    <Flex justify="space-between" align="center">
                        <Flex gap={10}>
                            <Button
                                type="text"
                                icon={
                                    collapsed ? (
                                        <MenuUnfoldOutlined />
                                    ) : (
                                        <MenuFoldOutlined />
                                    )
                                }
                                onClick={() => setCollapsed(!collapsed)}
                                style={{
                                    fontSize: "16px",
                                    width: 64,
                                    height: 64
                                }}
                            />
                            {documentName}
                        </Flex>
                        {status === "authenticated" && (
                            <div className="mr-4">
                                <Dropdown
                                    menu={{
                                        items: [
                                            {
                                                label: session.user?.email,
                                                key: "0"
                                            },
                                            {
                                                label: (
                                                    <Link
                                                        href={
                                                            process.env
                                                                .NEXT_PUBLIC_SERVER_URL! +
                                                            "/api/auth/signout"
                                                        }
                                                    >
                                                        Logout
                                                    </Link>
                                                ),
                                                key: "1"
                                            }
                                        ]
                                    }}
                                >
                                    <Avatar
                                        className="cursor-pointer"
                                        src={session?.user!.userInfo.image.data}
                                        alt="data:image/png;base64,"
                                    />
                                </Dropdown>
                            </div>
                        )}
                    </Flex>
                </Header>
                <Content
                    style={{
                        margin: "24px 16px",
                        padding: 24,
                        minHeight: 280,
                        background: colorBgContainer,
                        overflow: "scroll"
                    }}
                >
                    {children}
                </Content>
            </Layout>
        </Layout>
    )
}

export default SideMenu
