"use client"

import CustomSpin from "@/components/CustomSpin"
import NovuComponent from "@/components/NovuComponent"
import { MenuFoldOutlined, MenuUnfoldOutlined } from "@ant-design/icons"
import { faArchive, faFile, faUser } from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import {
    Avatar,
    Button,
    Dropdown,
    Flex,
    Image,
    Layout,
    Menu,
    Space,
    theme
} from "antd"
import { signOut, useSession } from "next-auth/react"
import { useEnvContext } from "next-runtime-env"
import Link from "next/link"
import { usePathname, useRouter } from "next/navigation"
import { ReactNode, useEffect, useState } from "react"
import { DocumentName } from "./_types/DocumentName"

const { Header, Sider, Content } = Layout

const CustomMenu = () => {
    const [selectedKey, setSelectedKey] = useState<string>("/bu/template")
    const router = useRouter()
    const pathName = usePathname()

    const switchRoute = (e: string) => {
        const location = window.location.origin
        router.push(`${location}/bu/${e}`)
    }
    useEffect(() => {
        setSelectedKey(pathName)
    }, [pathName])

    return (
        <Menu
            //theme="dark"
            mode="inline"
            selectedKeys={[selectedKey]}
            //defaultSelectedKeys={["1"]}
            /*  onClick={(e) => {
                switchRoute(e.key)
            }} */
            items={[
                {
                    key: "/bu/template",
                    icon: (
                        <Link href={"/bu/template"}>
                            <FontAwesomeIcon icon={faFile} />
                        </Link>
                    ),
                    label: "Biểu mẫu"
                },
                /*   {
                    key: "template/new",
                    icon: (
                        <Link href={"/bu/template/new"}>
                            <FontAwesomeIcon icon={faFileCirclePlus} />
                        </Link>
                    ),
                    label: "Tạo mới biểu mẫu"
                }, */
                {
                    key: "/bu/mywork",
                    icon: (
                        <Link href={"/bu/mywork"}>
                            <FontAwesomeIcon icon={faArchive} />
                        </Link>
                    ),
                    label: "Công việc của tôi"
                }
            ]}
        />
    )
}

const SideMenu = ({
    children,
    level
}: {
    children: ReactNode
    level: string
}) => {
    const [collapsed, setCollapsed] = useState(false)
    const [documentName, setDocumentName] = useState<string>("")
    const pathName = usePathname()
    const { status, data: session } = useSession()
    const {
        token: { colorBgContainer, colorPrimary }
    } = theme.useToken()
    const {
        NEXT_PUBLIC_NOVU_APPLICATION_IDENTIFIER,
        NEXT_PUBLIC_BACKEND_URL,
        NEXT_PUBLIC_SOCKET_URL
    } = useEnvContext()

    const onNotificationClick = (message: any) => {
        // your logic to handle the notification click
        if (message?.cta?.data?.url) {
            window.location.href = message.cta.data.url
        }
    }

    useEffect(() => {
        if (pathName.includes("template")) {
            setDocumentName(DocumentName.TEMPLATE)
        } else {
            setDocumentName(DocumentName.MYWORK)
        }
    }, [pathName])

    if (status === "loading")
        return (
            <CustomSpin
                noImage={true}
                theme={{
                    components: {
                        Spin: {
                            colorPrimary: "black"
                        }
                    }
                }}
            />
        )

    if (status === "unauthenticated")
        return (
            <p>
                Session expired.Please{" "}
                <Link className="nav-link" href="/api/auth/signin">
                    login
                </Link>{" "}
                again
            </p>
        )

    return (
        <Layout className="h-screen flex">
            <Sider trigger={null} collapsible collapsed={collapsed}>
                <center>
                    <Image
                        className="mt-2 mb-2"
                        width="40%"
                        preview={false}
                        src={`/img/hptIconKnowingIT.png`}
                        fallback="data:image/png;base64,"
                        alt="Icon"
                    />
                </center>
                <CustomMenu />
            </Sider>
            <Layout>
                <Header
                    style={{
                        padding: 0,
                        background: colorBgContainer
                    }}
                >
                    <Flex justify="space-between" align="center">
                        <Flex gap={10} align="center">
                            <Button
                                type="text"
                                icon={
                                    collapsed ? (
                                        <MenuUnfoldOutlined />
                                    ) : (
                                        <MenuFoldOutlined />
                                    )
                                }
                                onClick={() => setCollapsed(!collapsed)}
                                style={{
                                    fontSize: "16px",
                                    width: 64,
                                    height: 64
                                }}
                            />
                            <h1 className="text-xl" color={colorPrimary}>
                                {documentName}
                            </h1>
                        </Flex>

                        {status === "authenticated" && (
                            <Space className="mr-4">
                                <NovuComponent
                                    novuProps={{
                                        subscriberId: session.user.userInfo._id,
                                        applicationIdentifier:
                                            NEXT_PUBLIC_NOVU_APPLICATION_IDENTIFIER!,
                                        initialFetchingStrategy: {
                                            fetchNotifications: true,
                                            fetchUserPreferences: true
                                        },
                                        backendUrl: NEXT_PUBLIC_BACKEND_URL!,
                                        socketUrl: NEXT_PUBLIC_SOCKET_URL!
                                    }}
                                    popOverProps={{
                                        colorScheme: "light",
                                        onNotificationClick:
                                            onNotificationClick,
                                        showUserPreferences: false,
                                        footer: () => <></>
                                    }}
                                />
                                <Dropdown
                                    menu={{
                                        items: [
                                            {
                                                label: session.user?.userInfo
                                                    .mail,
                                                key: "0"
                                            },
                                            {
                                                label: (
                                                    <p
                                                        className="cursor-pointer"
                                                        onClick={async () =>
                                                            await signOut()
                                                        }
                                                    >
                                                        Đăng xuất
                                                    </p>
                                                ),
                                                key: "1"
                                            }
                                        ]
                                    }}
                                >
                                    <Avatar
                                        className="cursor-pointer"
                                        //src={session?.user!.userInfo.image.data}
                                        style={{
                                            backgroundColor: colorPrimary
                                        }}
                                        icon={<FontAwesomeIcon icon={faUser} />}
                                        alt="data:image/png;base64,"
                                    />
                                </Dropdown>
                            </Space>
                        )}
                    </Flex>
                </Header>
                <Content
                    style={{
                        margin: "24px 16px",
                        padding: 24,
                        minHeight: 280,
                        background: colorBgContainer,
                        overflowX: "hidden",
                        overflowY: "scroll"
                        //overflow: "hidden"
                    }}
                >
                    {children}
                </Content>
            </Layout>
        </Layout>
    )
}

export default SideMenu
