"use client"

import Filter from "@/app/users/teller/(components)/Filter/LayoutFilter"
import ButtonLogOut from "@/components/HeaderAvatar"
import { Image, Layout, Menu, theme } from "antd"
import React, { useState } from "react"

import ProviderMyworkDetail from "@/app/users/teller/(components)/provider/ProviderMyworkDetail"
import CustomSpin from "@/components/CustomSpin"
import { faArchive } from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { useSession } from "next-auth/react"
import Link from "next/link"
const { Header, Sider, Content } = Layout

const CustomMenu = ({ backgroundColor }: { backgroundColor: string }) => {
    return (
        <Menu
            style={{ backgroundColor }}
            defaultSelectedKeys={["mywork"]}
            items={[
                {
                    key: "mywork",
                    icon: (
                        <Link href={"/ksvteller/mywork"}>
                            ,
                            <FontAwesomeIcon icon={faArchive} />
                        </Link>
                    ),
                    label: "Công việc của tôi"
                }
            ]}
        />
    )
}
type Props = {
    children: React.ReactNode
    title: string
}
const SideMenu = async ({ children, title }: Props) => {
    const [collapsed, setCollapsed] = useState(false)
    const { status } = useSession()
    const {
        token: { colorBgContainer, colorPrimary }
    } = theme.useToken()

    if (status === "loading") {
        return (
            <CustomSpin
                noImage={true}
                theme={{
                    components: {
                        Spin: {
                            colorPrimary: "black"
                        }
                    }
                }}
            />
        )
    }
    if (status === "unauthenticated")
        return (
            <p>
                Session expired.Please{" "}
                <Link className="nav-link" href="api/auth/signin">
                    login
                </Link>{" "}
                again
            </p>
        )
    return (
        <Layout hasSider={true} className="h-screen">
            <Sider
                collapsible
                collapsed={collapsed}
                style={{ backgroundColor: colorPrimary }}
                onCollapse={(value) => setCollapsed(value)}
            >
                <center>
                    <Image
                        className="mt-2 mb-2"
                        width="40%"
                        preview={false}
                        src={`/img/hptIconKnowingIT.png`}
                        fallback="data:image/png;base64,"
                        alt="Icon"
                    />
                </center>
                <CustomMenu backgroundColor={colorPrimary} />
            </Sider>
            <Layout>
                <Header
                    className="flex items-center"
                    style={{
                        padding: "0 15px 0 15px",
                        background: colorBgContainer
                    }}
                >
                    <h1
                        className=" text-xl grow "
                        style={{
                            color: colorPrimary
                        }}
                    >
                        {title}
                    </h1>

                    <ButtonLogOut />
                </Header>
                <Content
                    style={{
                        borderRadius: "10px",
                        margin: "24px 16px",
                        padding: 20,
                        minHeight: 280,
                        background: colorBgContainer,
                        overflowY: "scroll"
                    }}
                >
                    <ProviderMyworkDetail>
                        <div className="my-6">
                            <Filter rootPath="ksvteller" />
                        </div>
                        <div>{children}</div>
                    </ProviderMyworkDetail>
                </Content>
            </Layout>
        </Layout>
    )
}

export default SideMenu
